import React, { useContext, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function FinishBtn() {
  const history = useHistory();
  const {
    // fetchProgressFood,
    fetchProgressDrink,
    doneRecipesLocal,
  } = useContext(Context);
  const [isFinish, setIsFinish] = useState(false);

  const handleFinish = () => {
    console.log(fetchProgressDrink);
    doneRecipesLocal({
      id: fetchProgressDrink.idMeal,
      type: 'food',
      nationality: fetchProgressDrink.strArea,
      category: fetchProgressDrink.strCategory,
      alcoholicOrNot: '',
      name: fetchProgressDrink.strMeal,
      image: fetchProgressDrink.strMealThumb,
      doneDate: fetchProgressDrink.dateModified,
      tags: [
        fetchProgressDrink.strTags
          && fetchProgressDrink.strTags.filter((_, i) => i < '2'),
      ],
    });
    history.push('/done-recipes');
  };

  const verifaInput = () => {
    const checked = document.querySelectorAll('.checkd-ui');
    for (let index = 0; index < checked.length; index += 1) {
      const element = checked[index].checked;
      if (element) {
        setIsFinish(false);
      } else {
        setIsFinish(true);
      }
    }
  };

  return (
    <div>
      <button
        type="button"
        className="btn-start-recipe"
        data-testid="finish-recipe-btn"
        onClick={ () => handleFinish() }
        disabled={ isFinish }
      >
        Finish recipe
      </button>
    </div>
  );
}

export default FinishBtn;
