import React, { useEffect, useState } from 'react';
import Header from '../components/Header';

function DoneRecipes() {
  const done = 'Done Recipes';
  const bool = false;
  const [localDone, setLocalDone] = useState([]);

  useEffect(() => {
    const doneRecipes = JSON.parse(localStorage.getItem('doneRecipes'));
    setLocalDone(doneRecipes);
  }, [localDone]);

  return (
    <div>
      <Header title={ done } hasSearch={ bool } />
      <div>
        <button type="button" data-testid="filter-by-all-btn">
          All
        </button>
        <button type="button" data-testid="filter-by-food-btn">
          Food
        </button>
        <button type="button" data-testid="filter-by-drink-btn">
          Drinks
        </button>
      </div>
      <div>
        {localDone.map((item, i) => (
          <div key={ i }>
            <img
              src={ item.image }
              alt="imagem"
              data-testid={ `${i}-horizontal-image` }
            />
            <h4 data-testid={ `${i}-horizontal-top-text` }>{ item.category }</h4>
            <h1 data-testid={ `${i}-horizontal-name` }>{ item.name }</h1>
            <h1 data-testid={ `${i}-horizontal-done-date` }>{ item.doneDate }</h1>
            <h1 data-testid={ `${i}-horizontal-tag` }>{ item.tags }</h1>
          </div>
        ))}
      </div>
    </div>
  );
}

export default DoneRecipes;
