import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import FiltersBtns from '../components/FiltersBtns';
import Header from '../components/Header';
import Context from '../context/context';

// import { Container } from './styles';

function FavoritesRecipes() {
  const favorite = 'Favorite Recipes';
  const bool = false;
  const { favoritesArray } = useContext(Context);
  console.log(favoritesArray);
  return (
    <div>
      <Header title={ favorite } hasSearch={ bool } />
      <FiltersBtns />
      <div>
        {localDone.map((item, i) => (
          <div key={ i }>
            <Link
              to={ verifyType(item) === false
                ? `/drinks/${item.id}`
                : `/foods/${item.id}` }
            >
              <img
                src={ item.image }
                alt="imagem"
                data-testid={ `${i}-horizontal-image` }
                className="img-done"
              />
              <h1 data-testid={ `${i}-horizontal-name` }>{item.name}</h1>
            </Link>
            <h4
              data-testid={ `${i}-horizontal-top-text` }
            >
              {item.nationality}
              {' '}
              -
              {' '}
              {item.category}
            </h4>
            <h3
              data-testid={ `${i}-horizontal-top-text` }
            >
              { (item.alcoholicOrNot) ? item.alcoholicOrNot : null }

            </h3>

            <h3 data-testid={ `${i}-horizontal-done-date` }>{item.doneDate}</h3>
            {item.tags.map((e) => (
              <h4 data-testid={ `${i}-${e}-horizontal-tag` } key={ e }>
                {e}
              </h4>
            ))}

            {!isCopy ? <input
              type="image"
              src={ share }
              data-testid={ `${i}-horizontal-share-btn` }
              alt="searchIcon"
              onClick={ handleShare }
            /> : 'Link copied!'}
          </div>
        ))}
      </div>
    </div>
  );
}

export default FavoritesRecipes;
