import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Context from './context';

function ProviderRecipes({ children }) {
  const [loginInput, setLogin] = useState({
    email: '',
    password: '',
  });
  const [disabledBtn, setDisabledBtn] = useState(true);
  const [radios, setRadios] = useState('');

  const handleChange = ({ target: { name, value } }) => {
    setLogin((oldState) => ({ ...oldState, [name]: value }));
    const tamMin = 6;
    const regex = /\w+@[a-z]+.com/g;
    if (loginInput.email.match(regex) && loginInput.password.length >= tamMin) {
      setDisabledBtn(false);
    } else {
      return setDisabledBtn(true);
    }
  };

  const getRecipes = async (param) => {
    if (radios === 'ingredient') {
      const url = `https://www.themealdb.com/api/json/v1/1/filter.php?i=${param}`;
      const getFetch = await fetch(url);
      const dataJson = await getFetch.json();
      return dataJson;
    }
    if (radios === 'name') {
      const url = `https://www.themealdb.com/api/json/v1/1/search.php?s=${param}`;
      const getFetch = await fetch(url);
      const dataJson = await getFetch.json();
      return dataJson;
    }
    if (radios === 'firstLetter') {
      try {
        const url = `https://www.themealdb.com/api/json/v1/1/search.php?f=${param}`;
        const getFetch = await fetch(url);
        const dataJson = await getFetch.json();
        return dataJson;
      } catch (error) {
        alert(error);
      }
    }
  };

  const contextValue = {
    loginInput,
    handleChange,
    disabledBtn,
    setRadios,
    radios,
    getRecipes,
  };

  return (
    <Context.Provider value={ contextValue }>
      { children }
    </Context.Provider>
  );
}

ProviderRecipes.propTypes = { children: PropTypes.node.isRequired };

export default ProviderRecipes;
