import React, { useContext } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function StartRecipe() {
  const history = useHistory();
  const { id } = useParams();
  const { ingredientsArray } = useContext(Context);

  const handleStartBtn = () => {
    if (history.location.pathname === `/foods/${id}`) {
      history.push(`/foods/${id}/in-progress`);
    }
    if (history.location.pathname === `/drinks/${id}`) {
      const objFavorites = {
        id: fetchIdDrink.idDrink,
        type: 'drink',
        nationality: '',
        category: fetchIdDrink.strCategory,
        alcoholicOrNot: fetchIdDrink.strAlcoholic,
        name: fetchIdDrink.strDrink,
        image: fetchIdDrink.strDrinkThumb,
        ingredient: ingredientsArray,
      };
      let joao = [];
      if (favoritesArray.length
      && favoritesArray.some((item) => item.id === id)) {
        setIsFavorite(true);
      } else {
        setIsFavorite(false);
      }
      if (
        favoritesArray.length
      && favoritesArray.some((item) => item.id === id)
      ) {
        joao = favoritesArray.filter((e) => e.id !== objFavorites.id);
        localStorage.setItem('doneRecipes', JSON.stringify(joao));
        setFavoritesArray(joao);
      } else {
        joao = [...favoritesArray, objFavorites];
        localStorage.setItem('doneRecipes', JSON.stringify(joao));
        setFavoritesArray(joao);
      }
      history.push(`/drinks/${id}/in-progress`);
    }
  };

  return (
    <div>
      <button
        className="btn-start-recipe"
        type="button"
        data-testid="start-recipe-btn"
        onClick={ handleStartBtn }
      >
        Start Recipe
      </button>
    </div>
  );
}

export default StartRecipe;
