import React, { useEffect, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function Recipes() {
  const {
    setTwelveFood,
    setTwelveDrink,
    twelveFood,
    twelveDrink,
    setBtnFetchFood,
    setBtnFetchDrink,
    btnFetchDrink,
    btnFetchFood,
  } = useContext(Context);
  const history = useHistory();

  const twelveFetchFood = async () => {
    const url = 'https://www.themealdb.com/api/json/v1/1/search.php?s=';
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const json = dataJson.meals.filter((_, i) => i < '12');
    setTwelveFood(json);
  };

  const twelveFetchDrink = async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s=';
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const json = dataJson.drinks.filter((_, i) => i < '12');
    setTwelveDrink(json);
  };

  const handleBtnFetchFood = async () => {
    const url = 'https://www.themealdb.com/api/json/v1/1/list.php?c=list';
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const json = dataJson.meals.filter((_, i) => i < '5');
    setBtnFetchFood(json);
  };

  const handleBtnFetchDrink = async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/list.php?c=list';
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const json = dataJson.drinks.filter((_, i) => i < '5');
    setBtnFetchDrink(json);
  };

  useEffect(() => {
    if (history.location.pathname === '/foods') {
      twelveFetchFood();
      handleBtnFetchFood();
      setBtnFetchDrink([]);
      setTwelveDrink([]);
    }
    if (history.location.pathname === '/drinks') {
      twelveFetchDrink();
      handleBtnFetchDrink();
      setBtnFetchFood([]);
      setTwelveFood([]);
    }
  }, []);

  const handleClickCategory = ({ target: { value } }) => {
    const filterCategory = twelveFood.filter(
      (elemento) => elemento.strCategory,
    );
    console.log(value);
    const filterTwelve = filterCategory.filter((_, i) => i < '12');
    setTwelveFood(filterTwelve);
  };

  return (
    <div className="recipes-page">
      <div className="btns">
        {btnFetchFood.map((item, i) => (
          <div key={ i } className="btnMeals">
            <button
              type="button"
              data-testid={ `${item.strCategory}-category-filter` }
              onClick={ handleClickCategory }
            >
              {item.strCategory}
            </button>
          </div>
        ))}
        {btnFetchDrink.map((item, i) => (
          <div key={ i }>
            <button
              type="button"
              data-testid={ `${item.strCategory}-category-filter` }
              className="drinkBtn"
            >
              {item.strCategory}
            </button>
          </div>
        ))}
      </div>
      <div className="itens">
        {twelveFood.map((item, i) => (
          <div data-testid={ `${i}-recipe-card` } key={ i }>
            <img
              src={ item.strMealThumb }
              alt="foto-receita"
              data-testid={ `${i}-card-img` }
            />
            <h2 data-testid={ `${i}-card-name` }>{item.strMeal}</h2>
          </div>
        ))}
        {twelveDrink.map((item, i) => (
          <div data-testid={ `${i}-recipe-card` } key={ i }>
            <img
              src={ item.strDrinkThumb }
              alt="foto-receita"
              data-testid={ `${i}-card-img` }
            />
            <h2 data-testid={ `${i}-card-name` }>{item.strDrink}</h2>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Recipes;
