import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function FinishBtn() {
  const history = useHistory();
  const { id } = useParams();
  const { testes,
    fetchProgressFood,
    ingredientsArray,
    fetchProgressDrink,
    doneRecipesArray,
    setDoneRecipesArray,
    amountArray } = useContext(Context);
  const [isFinish, setIsFinish] = useState(true);
  console.log(fetchProgressDrink);

  const handleDoneRecipesFood = () => {
    const objInprogress = {
      id: fetchProgressFood.idMeal,
      type: 'food',
      nationality: fetchProgressFood.strArea,
      category: fetchProgressFood.strCategory,
      alcoholicOrNot: '',
      name: fetchProgressFood.strMeal,
      image: fetchProgressFood.strMealThumb,
      amount: amountArray,
      ingredient: ingredientsArray,
      tags: [],
    };
    const novoArray = [...doneRecipesArray, objInprogress];
    localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
    setDoneRecipesArray(novoArray);
  };

  const handleDoneRecipesDrink = () => {
    const objInprogress = {
      id: fetchProgressDrink.idDrink,
      type: 'drink',
      nationality: '',
      category: fetchProgressDrink.strCategory,
      alcoholicOrNot: fetchProgressDrink.strAlcoholic,
      name: fetchProgressDrink.strDrink,
      image: fetchProgressDrink.strDrinkThumb,
      ingredient: ingredientsArray,
      amount: amountArray,
      doneDate: fetchProgressDrink,
    };
    const novoArray = [...doneRecipesArray, objInprogress];
    localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
    setDoneRecipesArray(novoArray);
  };

  const handleFinish = () => {
    if (history.location.pathname === `/foods/${id}/in-progress`) {
      handleDoneRecipesFood();
      history.push('/done-recipes');
    }
    if (history.location.pathname === `/drinks/${id}/in-progress`) {
      handleDoneRecipesDrink();
      history.push('/done-recipes');
    }
  };

  useEffect(() => {
    const responseLocal = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const input = document.querySelectorAll('.checkd-ui');
    if (
      responseLocal
      && responseLocal[0].checkeds.length > 0
      && input.length === responseLocal[0].checkeds.length
    ) {
      setIsFinish(false);
    }
  }, [testes]);

  return (
    <div>
      <button
        type="button"
        className="btn-start-recipe"
        data-testid="finish-recipe-btn"
        onClick={ handleFinish }
        disabled={ isFinish }
      >
        Finish recipe
      </button>
    </div>
  );
}

export default FinishBtn;
