import React, { useContext, useEffect } from 'react';
import copy from 'clipboard-copy';
import { useHistory, useParams } from 'react-router-dom';
import Context from '../context/context';
import share from '../images/shareIcon.svg';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';

// import { Container } from './styles';

function DrinksIdProgress() {
  const history = useHistory();
  const { id } = useParams();
  const {
    fetchIdDrink,
    isCopy,
    setCopy,
    favoritesArray,
    isFavorite,
    setIsFavorite,
    setFavoritesArray } = useContext(Context);
  console.log('opa, eu sou o rapaz', fetchIdDrink);
  const inProgress = JSON.parse(localStorage.getItem('inProgressRecipes'));
  const handleShare = () => {
    copy(`http://localhost:3000${history.location.pathname}`);
    setCopy(true);
    console.log('Eu sou o inpro', inProgreess);
  };

  const handleFavoritesDrink = () => {
    const objFavorites = {
      id: fetchIdDrink.idDrink,
      type: 'drink',
      nationality: '',
      category: fetchIdDrink.strCategory,
      alcoholicOrNot: fetchIdDrink.strAlcoholic,
      name: fetchIdDrink.strDrink,
      image: fetchIdDrink.strDrinkThumb,
    };
    let joao = [];
    if (favoritesArray.length
      && favoritesArray.some((item) => item.id === id)) {
      setIsFavorite(true);
    } else {
      setIsFavorite(false);
    }
    if (
      favoritesArray.length
      && favoritesArray.some((item) => item.id === id)
    ) {
      joao = favoritesArray.filter((e) => e.id !== objFavorites.id);
      localStorage.setItem('favoriteRecipes', JSON.stringify(joao));
      setFavoritesArray(joao);
    } else {
      joao = [...favoritesArray, objFavorites];
      localStorage.setItem('favoriteRecipes', JSON.stringify(joao));
      setFavoritesArray(joao);
    }
  };

  useEffect(() => {
    if (favoritesArray.some((item) => item.id === id)) {
      setIsFavorite(false);
    }
  }, [favoritesArray, id, setIsFavorite]);

  return (
    <div>
      <img
        src={ inProgress[0].image }
        alt="foto-drink"
        data-testid="recipe-photo"
        className="details-img-drink"
      />
      <h1 data-testid="recipe-title">{inProgress[0].name}</h1>
      <h2 data-testid="recipe-category">{inProgress[0].alcoholicOrNot}</h2>
      <button
        type="button"
        data-testid="share-btn"
        onClick={ handleShare }
        className="share-btn"
      >
        {!isCopy ? <img src={ share } alt="share" /> : 'Link copied!'}
      </button>
      <button type="button" onClick={ handleFavoritesDrink }>
        <img
          src={ !isFavorite ? blackHeartIcon : whiteHeartIcon }
          alt="blackHeart"
          data-testid="favorite-btn"
        />
      </button>
      <ol>
        {inProgress[0].ingredient.map(
          (item, i) => item && (
            <li data-testid={ `${i}-ingredient-name-and-measure` } key={ i }>
              {`${inProgress[0].amount[i]} ${item}`}
            </li>
          ),
        )}
        <p data-testid="instructions">{inProgress[0].strInstructions}</p>
      </ol>
      <button type="button" data-testid="finish-recipe-btn">Finish recipe</button>
    </div>
  );
}

export default DrinksIdProgress;
