import React, { useContext, useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function FinishBtn() {
  const history = useHistory();
  //   const { id } = useParams();
  const { testes,
    // fetchProgressFood,
    // ingredientsArray,
    fetchProgressDrink,
    ingredientsArray,
    // doneRecipesArray,
    // setDoneRecipesArray,
    amountArray,
  } = useContext(Context);
  const [isFinish, setIsFinish] = useState(true);

  //   const handleDoneRecipesFood = () => {
  //     const objInprogress = {
  //       id: fetchProgressFood.idMeal,
  //       type: 'food',
  //       nationality: fetchProgressFood.strArea,
  //       category: fetchProgressFood.strCategory,
  //       alcoholicOrNot: '',
  //       name: fetchProgressFood.strMeal,
  //       image: fetchProgressFood.strMealThumb,
  //       amount: amountArray,
  //       ingredient: ingredientsArray,
  //       doneDate: fetchProgressFood.dateModified,
  //       tags: [...fetchProgressFood.strTags.filter((_, i) => i < '2')],
  //     };
  //     const novoArray = [...doneRecipesArray, objInprogress];
  //     localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
  //     setDoneRecipesArray(novoArray);
  //   };

  const handleFinish = () => {
    history.push('/done-recipes');
    doneRecipesLocal({
      id: fetchProgressDrink.idMeal,
      type: 'food',
      nationality: fetchProgressDrink.strArea,
      category: fetchProgressDrink.strCategory,
      alcoholicOrNot: '',
      name: fetchProgressDrink.strMeal,
      image: fetchProgressDrink.strMealThumb,
      amount: amountArray,
      ingredient: ingredientsArray,
      doneDate: fetchProgressDrink.dateModified,
      tags: [...fetchProgressDrink.strTags.filter((_, i) => i < '2')],
    });
  };

  const doneRecipesLocal = (objInprogress) => {
    const novoArray = JSON.parse(localStorage.getItem('doneRecipes'));
    if (
      novoArray
    ) {
      localStorage.setItem('doneRecipes', JSON.stringify([...novoArray, objInprogress]));
    } else {
      localStorage.setItem('doneRecipes', JSON.stringify([objInprogress]));
    }
  };

  useEffect(() => {
    const responseLocal = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const input = document.querySelectorAll('.checkd-ui');
    if (
      responseLocal
      && responseLocal[0].checkeds.length > 0
      && input.length === responseLocal[0].checkeds.length
    ) {
      setIsFinish(false);
    }
  }, [testes]);

  return (
    <div>
      <button
        type="button"
        className="btn-start-recipe"
        data-testid="finish-recipe-btn"
        onClick={ handleFinish }
        disabled={ isFinish }
      >
        Finish recipe
      </button>
    </div>
  );
}

export default FinishBtn;
