import React, { useContext, useEffect, useState } from 'react';
import { useParams, Link, useHistory } from 'react-router-dom';
import copy from 'clipboard-copy';
import StartRecipe from '../components/StartRecipe';
import Context from '../context/context';
import share from '../images/shareIcon.svg';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';

// import { Container } from './styles';

function FoodsId() {
  const { setFetchId, fetchId } = useContext(Context);
  const { id } = useParams();
  const history = useHistory();
  const [isCopy, setCopy] = useState(false);
  const [isFavorite, setIsFavorite] = useState(false);

  const [ingredientsArrayFood, setIngredientsArrayFood] = useState([]);
  const [amountArray, setAmmount] = useState([]);
  const [drinkArray, setDrink] = useState([]);
  const [favoritesArray, setFavoritesArray] = useState([]);

  const handleFetchIdFood = async () => {
    const url = `https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`;
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const responseIdArrayFood = dataJson.meals[0];
    const reduce = Object.entries(responseIdArrayFood).reduce((acc, item) => {
      if (item[0].includes('strIngredient')) {
        acc.push(item[1]);
      }
      return acc;
    }, []);
    const ammoutReduce = Object.entries(responseIdArrayFood).reduce(
      (acc, item) => {
        if (item[0].includes('strMeasure')) {
          acc.push(item[1]);
        }
        return acc;
      },
      [],
    );

    setAmmount(ammoutReduce);
    setIngredientsArrayFood(reduce);
    setFetchId(responseIdArrayFood);
  };

  const recommendationFetch = async () => {
    const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s=';
    const response = await fetch(url);
    const data = await response.json();
    setDrink(data.drinks.filter((_, i) => i < '6'));
  };

  useEffect(() => {
    handleFetchIdFood();
    recommendationFetch();
    const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    if (favorites && favorites.some((item) => item.id === id)) {
      setIsFavorite(true);
    }
  }, []);

  const handleShare = () => {
    copy(`http://localhost:3000${history.location.pathname}`);
    setCopy(true);
  };

  const handleFavorites = () => {
    const objFavorites = {
      id: fetchId.idMeal,
      type: 'food',
      nationality: fetchId.strArea,
      category: fetchId.strCategory,
      alcoholicOrNot: '',
      name: fetchId.strMeal,
      image: fetchId.strMealThumb,
    };
    console.log('favorites antes', favoritesArray);
    if (favoritesArray && favoritesArray.some((item) => item.id === id)) {
      const arrayAtualizado = favoritesArray.filter(
        (item) => item.id !== fetchId.id,
      );
      localStorage.setItem('favoriteRecipes', JSON.stringify(arrayAtualizado));
      console.log('array', arrayAtualizado);
      setFavoritesArray(arrayAtualizado);
    } else if (!localStorage.getItem('favoriteRecipes')) {
      localStorage.setItem('favoriteRecipes', JSON.stringify([objFavorites]));
      setFavoritesArray([objFavorites]);
    } else {
      localStorage.setItem(
        'favoriteRecipes',
        JSON.stringify([...favoritesArray, objFavorites]),
      );
      setFavoritesArray([...favorites, objFavorites]);
    }

    // if (!localStorage.getItem('favoriteRecipes')) {
    //   localStorage.setItem('favoriteRecipes', JSON.stringify([objFavorites]));
    // } else {
    //   const favorites = JSON.parse(localStorage.getItem('favoriteRecipes'));
    //   localStorage.setItem(
    //     'favoriteRecipes',
    //     JSON.stringify([...favorites, objFavorites]),
    //   );
    // }
  };

  return (
    <div>
      <div>
        <img
          src={ fetchId.strMealThumb }
          alt="foto-drink"
          data-testid="recipe-photo"
          className="details-img-drink"
        />
        <h1 data-testid="recipe-title">{fetchId.strMeal}</h1>
        <h2 data-testid="recipe-category">{fetchId.strCategory}</h2>
        <ol>
          {ingredientsArrayFood.map(
            (item, i) => item && (
              <li data-testid={ `${i}-ingredient-name-and-measure` } key={ i }>
                {`${amountArray[i]} ${item}`}
              </li>
            ),
          )}
          <p data-testid="instructions">{fetchId.strInstructions}</p>
        </ol>
        {fetchId.strYoutube && (
          <iframe
            title="youtube"
            src={ fetchId.strYoutube.replace('watch?v=', 'embed/') }
            data-testid="video"
          />
        )}
        <div className="recommendationItens">
          {drinkArray.map((item, i) => (
            <div data-testid={ `${i}-recomendation-card` } key={ i }>
              <Link to={ `/drinks/${item.idDrink}` } key={ i }>
                <div data-testid={ `${i}-recipe-card` }>
                  <img
                    src={ item.strDrinkThumb }
                    alt="foto-receita"
                    data-testid={ `${i}-card-img` }
                    className="recommendationImage"
                  />
                  <h2 data-testid={ `${i}-recomendation-title` }>
                    {item.strDrink}
                  </h2>
                </div>
              </Link>
            </div>
          ))}
        </div>
      </div>
      <button
        type="button"
        data-testid="share-btn"
        onClick={ handleShare }
        className="share-btn"
      >
        {!isCopy ? <img src={ share } alt="share" /> : 'Link copied!'}
      </button>
      <button type="button" onClick={ handleFavorites }>
        {isFavorite ? (
          <img
            src={ blackHeartIcon }
            alt="blackHeart"
            data-testid="favorite-btn"
          />
        ) : (
          <img
            src={ whiteHeartIcon }
            alt="whiteHeart"
            data-testid="favorite-btn"
          />
        )}
      </button>
      <StartRecipe />
    </div>
  );
}

export default FoodsId;
