import React, { useContext, useEffect, useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import Context from '../context/context';

// import { Container } from './styles';

function FinishBtn() {
  const history = useHistory();
  const { id } = useParams();
  const { testes,
    fetchProgressFood,
    ingredientsArray,
    fetchProgressDrink,
    inProgressArrayFood,
    setInProgressArrayFood,
    setInProgressArrayDrink,
    inProgressArrayDrink,
    amountArray } = useContext(Context);
  const [isFinish, setIsFinish] = useState(true);

  const handleDoneRecipesFood = () => {
    const objInprogress = {
      id: fetchProgressFood.idMeal,
      type: 'food',
      nationality: fetchProgressFood.strArea,
      category: fetchProgressFood.strCategory,
      alcoholicOrNot: '',
      name: fetchProgressFood.strMeal,
      image: fetchProgressFood.strMealThumb,
      amount: amountArray,
      ingredient: ingredientsArray,
      checkeds: [],
    };
    let novoArray = [];
    if (
      inProgressArrayFood.length
            && inProgressArrayFood.some((item) => item.id === id)
    ) {
      novoArray = inProgressArrayFood.filter((e) => e.id !== objInprogress.id);
      localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
      setInProgressArrayFood(novoArray);
    } else {
      novoArray = [...inProgressArrayFood, objInprogress];
      localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
      setInProgressArrayFood(novoArray);
    }
  };

  //   const handleDoneRecipesDrink = () => {
  //     const objInprogress = {
  //       id: fetchProgressDrink.idDrink,
  //       type: 'drink',
  //       nationality: '',
  //       category: fetchProgressDrink.strCategory,
  //       alcoholicOrNot: fetchProgressDrink.strAlcoholic,
  //       name: fetchProgressDrink.strDrink,
  //       image: fetchProgressDrink.strDrinkThumb,
  //       ingredient: ingredientsArray,
  //       amount: amountArray,
  //       checkeds: [],
  //     };
  //     let novoArray = [];
  //     if (
  //       inProgressArrayDrink.length
  //             && inProgressArrayDrink.some((item) => item.id === id)
  //     ) {
  //       novoArray = inProgressArrayDrink.filter((e) => e.id !== objInprogress.id);
  //       localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
  //       setInProgressArrayDrink(novoArray);
  //     } else {
  //       novoArray = [...inProgressArrayDrink, objInprogress];
  //       localStorage.setItem('doneRecipes', JSON.stringify(novoArray));
  //       setInProgressArrayDrink(novoArray);
  //     }
  //   };

  const handleFinish = () => {
    if (history.location.pathname === `/foods/${id}/in-progress`) {
      handleDoneRecipesFood();
    }
    // if (history.location.pathname === `/drinks/${id}/in-progress`) {
    //   handleDoneRecipesDrink();
    // }
    history.push('/done-recipes');
  };

  useEffect(() => {
    const responseLocal = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const input = document.querySelectorAll('.checkd-ui');
    if (
      responseLocal
      && responseLocal[0].checkeds.length > 0
      && input.length === responseLocal[0].checkeds.length
    ) {
      setIsFinish(false);
    }
  }, [testes]);

  return (
    <div>
      <button
        type="button"
        data-testid="finish-recipe-btn"
        onClick={ handleFinish }
        disabled={ isFinish }
      >
        Finish recipe
      </button>
    </div>
  );
}

export default FinishBtn;
