import React, { useContext, useEffect, useState } from 'react';
import copy from 'clipboard-copy';
import { useHistory, useParams } from 'react-router-dom';
import Context from '../context/context';
import share from '../images/shareIcon.svg';
import whiteHeartIcon from '../images/whiteHeartIcon.svg';
import blackHeartIcon from '../images/blackHeartIcon.svg';
import '../styles/RecipeDetail.css';

// import { Container } from './styles';

function DrinksIdProgress() {
  const history = useHistory();
  const { id } = useParams();
  const {
    isCopy,
    setCopy,
    favoritesArray,
    isFavorite,
    setIsFavorite,
    fetchProgressFood,
    setFetchProgressFood,
    ingredientsArray,
    amountArray,
    setAmmount,
    setIngredients,
    inProgressArrayFood,
    setFavoritesArray,
    setInProgressArrayFood,
  } = useContext(Context);
  const [isFinish, setIsFinish] = useState(true);
  const [testes, setTeste] = useState(false);

  const objInprogressFood = {
    id: fetchId.idMeal,
    type: 'food',
    nationality: fetchId.strArea,
    category: fetchId.strCategory,
    alcoholicOrNot: '',
    name: fetchId.strMeal,
    image: fetchId.strMealThumb,
    amount: amountArray,
    ingredient: ingredientsArrayFood,
    checkeds: [],
  };

  const handleShare = () => {
    copy(
      `http://localhost:3000${history.location.pathname.replace(
        '/in-progress',
        '',
      )}`,
    );
    setCopy(true);
  };

  const handleFavoritesDrink = () => {
    const objFavorites = {
      id: fetchProgressFood.idMeal,
      type: 'food',
      nationality: fetchProgressFood.strArea,
      category: fetchProgressFood.strCategory,
      alcoholicOrNot: '',
      name: fetchProgressFood.strMeal,
      image: fetchProgressFood.strMealThumb,
    };
    let joao = [];
    if (
      favoritesArray.length
      && favoritesArray.some((item) => item.id === id)
    ) {
      setIsFavorite(true);
    } else {
      setIsFavorite(false);
    }
    if (
      favoritesArray.length
      && favoritesArray.some((item) => item.id === id)
    ) {
      joao = favoritesArray.filter((e) => e.id !== objFavorites.id);
      localStorage.setItem('favoriteRecipes', JSON.stringify(joao));
      setFavoritesArray(joao);
    } else {
      joao = [...favoritesArray, objFavorites];
      localStorage.setItem('favoriteRecipes', JSON.stringify(joao));
      setFavoritesArray(joao);
    }
  };

  const handleInProgress = async () => {
    const url = `https://www.themealdb.com/api/json/v1/1/lookup.php?i=${id}`;
    const getFetch = await fetch(url);
    const dataJson = await getFetch.json();
    const responseIdArray = dataJson.meals[0];
    const reduce = Object.entries(responseIdArray).reduce((acc, item) => {
      if (item[0].includes('strIngredient')) {
        acc.push(item[1]);
      }
      return acc;
    }, []);
    const ammoutReduce = Object.entries(responseIdArray).reduce((acc, item) => {
      if (item[0].includes('strMeasure')) {
        acc.push(item[1]);
      }
      return acc;
    }, []);

    setAmmount(ammoutReduce);
    setIngredients(reduce);
    setFetchProgressFood(responseIdArray);
  };

  const handleChecked = ({ target }) => {
    setTeste(!testes);
    const { checked } = target;
    if (inProgressArrayFood && checked) {
      target.parentElement.style.textDecoration = 'line-through';
      const checkedIngredients = target.parentElement.innerText;
      const newInProgress = inProgressArrayFood;
      const indexProgress = inProgressArrayFood.findIndex(
        (item) => item.id === objInprogressFood.id,
      ) || 0;
      if (newInProgress[0]) {
        newInProgress[indexProgress].checkeds.push(checkedIngredients);
        localStorage.setItem(
          'inProgressRecipes',
          JSON.stringify(newInProgress),
        );
      }
    } else {
      target.parentElement.style.textDecoration = 'none';
    }
  };

  useEffect(() => {
    handleInProgress();
    if (favoritesArray.some((item) => item.id === id)) {
      setIsFavorite(false);
    }
  }, []);

  useEffect(() => {
    let joao = [];
    if (
      inProgressArrayFood.length
      && inProgressArrayFood.some((item) => item.id === id)
    ) {
      joao = inProgressArrayFood.filter((e) => e.id !== objInprogressFood.id);
      localStorage.setItem('inProgressRecipes', JSON.stringify(joao));
      setInProgressArrayFood(joao);
    } else {
      joao = [...inProgressArrayFood, objInprogressFood];
      localStorage.setItem('inProgressRecipes', JSON.stringify(joao));
      setInProgressArrayFood(joao);
    }
  });
  // comentário para push

  useEffect(() => {
    const junim = JSON.parse(localStorage.getItem('inProgressRecipes'));
    const input = document.querySelectorAll('.checkd-ui');
    if (
      junim
      && junim[0].checkeds.length > 0
      && input.length === junim[0].checkeds.length
    ) {
      setIsFinish(false);
    }
  }, [testes]);

  const handleFinish = () => {
    history.push('/done-recipes');
  };

  return (
    <div>
      <div className="buttons">
        <button
          type="button"
          data-testid="share-btn"
          onClick={ handleShare }
          className="share-btn"
        >
          {!isCopy ? <img src={ share } alt="share" /> : 'Link copied!'}
        </button>
        <button type="button" onClick={ handleFavoritesDrink }>
          <img
            src={ !isFavorite ? blackHeartIcon : whiteHeartIcon }
            alt="blackHeart"
            data-testid="favorite-btn"
          />
        </button>
      </div>
      <div className="recipe-div">
        <img
          src={ fetchProgressFood.strMealThumb }
          alt="foto-drink"
          data-testid="recipe-photo"
          className="details-img"
        />
        <h1 data-testid="recipe-title">{fetchProgressFood.strMeal}</h1>
        <h2 data-testid="recipe-category">{fetchProgressFood.strCategory}</h2>
        <ol className="ingredients-inprogress">
          {ingredientsArray.map(
            (item, i) => item && (
              <li
                data-testid={ `${i}-ingredient-step` }
                key={ i }
              >
                {`${amountArray[i]} ${item} —`}
                <input
                  type="checkbox"
                  onChange={ handleChecked }
                  className="checkd-ui"
                />
              </li>
            ),
          )}
        </ol>
        <div className="recipe-end-div">
          <p className="instructions" data-testid="instructions">
            {fetchProgressFood.strInstructions}
          </p>
        </div>
      </div>
      <button
        className="btn-start-recipe"
        type="button"
        data-testid="finish-recipe-btn"
        onClick={ handleFinish }
        disabled={ isFinish }
      >
        Finish recipe
      </button>
    </div>
  );
}

export default DrinksIdProgress;
