import React, { useContext, useEffect, useState } from 'react';
import copy from 'clipboard-copy';
import { useParams } from 'react-router-dom';
import share from '../images/shareIcon.svg';
import Header from '../components/Header';
import Context from '../context/context';
import FiltersBtns from '../components/FiltersBtns';

function DoneRecipes() {
  const { isCopy, setCopy } = useContext(Context);
  const done = 'Done Recipes';
  const bool = false;
  const [localDone, setLocalDone] = useState([]);

  const handleShare = () => {
    const { id } = useParams();
    copy(
      `http://localhost:3000${id}`,
    );
    setCopy(true);
  };

  useEffect(() => {
    const doneRecipes = JSON.parse(localStorage.getItem('doneRecipes'));
    setLocalDone(doneRecipes);
  }, []);

  return (
    <div>
      <Header title={ done } hasSearch={ bool } />
      <FiltersBtns />
      <div>
        {localDone.map((item, i) => (
          <div key={ i }>
            <img
              src={ item.image }
              alt="imagem"
              data-testid={ `${i}-horizontal-image` }
              className="img-done"
            />
            <h4
              data-testid={ `${i}-horizontal-top-text` }
            >
              {item.nationality}
              {' '}
              -
              {' '}
              {item.category}
            </h4>
            <h3
              data-testid={ `${i}-horizontal-top-text` }
            >
              { (item.alcoholicOrNot) ? item.alcoholicOrNot : null }

            </h3>
            <h1 data-testid={ `${i}-horizontal-name` }>{item.name}</h1>
            <h1 data-testid={ `${i}-horizontal-done-date` }>{item.doneDate}</h1>
            {item.tags.map((e) => (
              <h4 data-testid={ `${i}-${e}-horizontal-tag` } key={ e }>
                {e}
              </h4>
            ))}

            {!isCopy ? <input
              type="image"
              src={ share }
              data-testid={ `${i}-horizontal-share-btn` }
              alt="searchIcon"
              onClick={ handleShare }
            /> : 'Link copied!'}
          </div>
        ))}
      </div>
    </div>
  );
}

export default DoneRecipes;
