import { REQ_API } from '../actions/actionTypes';

const INITIAL_STATE = {
  currencies: [], // array de string
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case REQ_API:
    return {
      ...state,
      currencies: action.payload,
    };
  default:
    return state;
  }
};

export default wallet;
