import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { requestAPI, requestApiTwo } from '../actions';

const INITIAL_STATE = {
  id: 0,
  value: '',
  descricao: '',
  moeda: 'USD',
  metodo: 'Dinheiro',
  tag: 'Alimentação',
};

class Wallet extends React.Component {
  state = {
    ...INITIAL_STATE,
  }

  componentDidMount() {
    const { fetchReq } = this.props;
    fetchReq();
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  handleClick = () => {
    const { fetchTwo } = this.props;
    const expenses = this.state;
    this.setState(() => {id: expenses.len});
    fetchTwo(expenses);
    this.setState({
      ...INITIAL_STATE,
    });
  }

  render() {
    const { email, currencies } = this.props;
    const { value, descricao, moeda, metodo, tag } = this.state;
    return (
      <div>
        <header>
          <div>
            <ul>
              <li data-testid="email-field">{email}</li>
              <li data-testid="total-field">0</li>
              <li data-testid="header-currency-field">BRL</li>
            </ul>
          </div>
        </header>
        <form>
          <label htmlFor="value">
            value
            <input
              id="value"
              name="value"
              type="text"
              value={ value }
              data-testid="value-input"
              placeholder="value"
              onChange={ this.handleChange }
            />
          </label>
          <label htmlFor="description-input">
            Descrição
            <input
              data-testid="description-input"
              id="description-input"
              name="descricao"
              value={ descricao }
              placeholder="Descrição"
              onChange={ this.handleChange }
            />
          </label>
          <label htmlFor="moeda">
            Moeda
            <select
              id="moeda"
              name="moeda"
              value={ moeda }
              onChange={ this.handleChange }
            >
              {currencies.map((item, index) => (
                <option key={ index }>{item}</option>
              ))}
            </select>
          </label>
          <label htmlFor="metodo">
            Método de pagamento
            <select
              data-testid="method-input"
              id="metodo"
              name="metodo"
              value={ metodo }
              onChange={ this.handleChange }
            >
              <option>Dinheiro</option>
              <option>Cartão de crédito</option>
              <option>Cartão de débito</option>
            </select>
          </label>
          <label htmlFor="tag">
            Despesa
            <select
              data-testid="tag-input"
              id="tag"
              value={ tag }
              name="tag"
              onChange={ this.handleChange }
            >
              <option>Alimentação</option>
              <option>Lazer</option>
              <option>Trabalho</option>
              <option>Transporte</option>
              <option>Saúde</option>
            </select>
          </label>
          <button
            type="button"
            onClick={ this.handleClick }
          >
            Adicionar despesa
          </button>
        </form>
      </div>
    );
  }
}

Wallet.propTypes = {
  email: PropTypes.string.isRequired,
  fetchReq: PropTypes.func.isRequired,
  currencies: PropTypes.arrayOf(PropTypes.string).isRequired,
  fetchTwo: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  email: state.user.email,
  currencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  fetchReq: () => dispatch(requestAPI()),
  fetchTwo: () => dispatch(requestApiTwo()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);
